    private void btnEvaluar1FerrariActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEvaluar1FerrariActionPerformed
        double a, b, c, d,cuarta;

        String f="x^4+x^3-0.56x^2-1.44x-2.88";
        
        a=1;
        b=-0.56;
        c=-1.44;
        d=-2.88;
        cuarta=1;

     if (cuarta != 1) {
            a = a / cuarta;
            b = b / cuarta;
            c = c / cuarta;
            d=d/cuarta;
            cuarta = 1;
        }

        double raiz[] = ProcesoTarFerrari.ferrari(a, b, c, d);

        txt1Raiz1.setText(String.valueOf(raiz[0]));
        txt2Raiz1.setText(String.valueOf(raiz[1]));
        txt3Raiz1.setText(String.valueOf(raiz[2]));
        txt4Raiz1.setText(String.valueOf(raiz[3]));
    }//GEN-LAST:event_btnEvaluar1FerrariActionPerformed

