    private void btnEvaluar2FerrariActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEvaluar2FerrariActionPerformed
        double a, b, c, d,cuarta;

 
        String f="2*x^4-3*x^2+5*x+2";
        
        a=0;
        b=-3;
        c=5;
        d=2;
        cuarta=2;

     if (cuarta != 1) {
            a = a / cuarta;
            b = b / cuarta;
            c = c / cuarta;
            d=d/cuarta;
            cuarta = 1;
        }

        double raiz[] = ProcesoTarFerrari.ferrari(a, b, c, d);

        txt1Raiz2.setText(String.valueOf(raiz[0]));
        txt2Raiz2.setText(String.valueOf(raiz[1]));
        txt3Raiz2.setText(String.valueOf(raiz[2]));
        txt4Raiz2.setText(String.valueOf(raiz[3]));
    }//GEN-LAST:event_btnEvaluar2FerrariActionPerformed

